namespace Common.Utilities
{
	/// <summary>
	/// Represents a set of regular expression utility methods. This class cannot be inherited.
	/// </summary>
	public static class RegexUtility
	{
		/// <summary>
		/// A <see cref="System.Guid"/>. This field is constant.
		/// </summary>
		/// <remarks>
		/// This field returns @"[0-9a-fA-F]{8}[-]?([0-9a-fA-F]{4}[-]?){3}[0-9a-fA-F]{12}".
		/// </remarks>
		public const string Guid = @"^[{|\(]?[0-9a-fA-F]{8}[-]?([0-9a-fA-F]{4}[-]?){3}[0-9a-fA-F]{12}[\)|}]?$";

		/// <summary>
		/// Internet URL regular expression.
		/// </summary>
		/// <remarks>
		/// This field returns "http://([\w-]+\.)+[\w-]+(/[\w- ./?%&amp;=]*)?".
		/// </remarks>
		public const string Url = @"http://([\w-]+\.)+[\w-]+(/[\w- ./?%&=]*)?";

		/// <summary>
		/// United States phone number regular expression. This field is constant.
		/// </summary>
		/// <remarks>
		/// This field returns "((\(\d{3}\) ?)|(\d{3}-))?\d{3}-\d{4}".
		/// </remarks>
		public const string UnitedStatesTelehoneNumberWithoutGroups = @"((\(\d{3}\) ?)|(\d{3}-))?\d{3}-\d{4}";

		//public const string REGEX_PHONE = @"^\+?[\d\s]{3,}$";
		//public const string REGEX_PHONE_WITH_AREA = @"^\+?[\d\s]+\(?[\d\s]{10,}$";

		/// <summary>
		/// United States social security number regular expression. This field is constant.
		/// </summary>
		/// <remarks>
		/// This field returns "\d{3}-\d{2}-\d{4}".
		/// </remarks>
		public const string UnitedStatesSocialSecurityNumber = @"\d{3}-\d{2}-\d{4}";

		/// <summary>
		/// United States ZIP code. This field is constant.
		/// </summary>
		/// <remarks>
		/// This field returns "\d{5}(-\d{4})?".
		/// </remarks>
		public const string UnitedStatesPostalCode = @"\d{5}(-\d{4})?";

		/// <summary>
		/// Either a United States or Canadian Postal Code
		/// </summary>
		public const string UnitedStatesOrCanadaPostalCode = @"(^\d{5}(-\d{4})?$|^[A-Za-z]\d[A-Za-z] ?\d[A-Za-z]\d$)";

		/// <summary>
		/// United States States. This field is a constant.
		/// </summary>
		/// This field returns "(:?A[KLRZ]|C[AOT]|D[CE]|FL|GA|HI|I[ADLN]|K[SY]|LA|M[ADEINOST]|N[CDEHJMVY]|O[HKR]|P[AR]|RI|S[CD]|T[NX]|UT|V[AIT]|W[AIVY])".
		public static readonly string UnitedStatesStates = @"(:?A[KLRZ]|C[AOT]|D[CE]|FL|GA|HI|I[ADLN]|K[SY]|LA|M[ADEINOST]|N[CDEHJMVY]|O[HKR]|P[AR]|RI|S[CD]|T[NX]|UT|V[AIT]|W[AIVY])";

		/// <summary>
		/// Password (6-15 characters, must begin with letter and end with a digit). This field is constant.
		/// </summary>
		/// <remarks>
		/// This field returns "^[a-zA-Z]\w{4,13}\d+$".ICM
		/// </remarks>
		public const string ComplexPassword = @"^[a-zA-Z]\w{4,13}\d+$";

		/// <summary>
		///	Represents an RFC 2822 internet email addresses.
		/// </summary>
		// This field returns ^((?>[a-zA-Z\d!#$%&'*+\-/=?^_`{|}~]+\x20*|"((?=[\x01-\x7f])[^"\\]|\\[\x01-\x7f])*"\x20*)*(?<angle><))?((?!\.)(?>\.?[a-zA-Z\d!#$%&'*+\-/=?^_`{|}~]+)+|"((?=[\x01-\x7f])[^"\\]|\\[\x01-\x7f])*")@(((?!-)[a-zA-Z\d\-]+(?<!-)\.)+[a-zA-Z]{2,}|\[(((?(?<!\[)\.)(25[0-5]|2[0-4]\d|[01]?\d?\d)){4}|[a-zA-Z\d\-]*[a-zA-Z\d]:((?=[\x01-\x7f])[^\\\[\]]|\\[\x01-\x7f])+)\])(?(angle)>)$</b>.
		public const string EmailAddress = "^((?>[a-zA-Z\\d!#$%&'*+\\-/=?^_`{|}~]+\\x20*|\"((?=[\\x01-\\x7f])[^\"\\\\]|\\\\[\\x01-\\x7f])*\"\\x20*)*(?<angle><))?((?!\\.)(?>\\.?[a-zA-Z\\d!#$%&'*+\\-/=?^_`{|}~]+)+|\"((?=[\\x01-\\x7f])[^\"\\\\]|\\\\[\\x01-\\x7f])*\")@(((?!-)[a-zA-Z\\d\\-]+(?<!-)\\.)+[a-zA-Z]{2,}|\\[(((?(?<!\\[)\\.)(25[0-5]|2[0-4]\\d|[01]?\\d?\\d)){4}|[a-zA-Z\\d\\-]*[a-zA-Z\\d]:((?=[\\x01-\\x7f])[^\\\\\\[\\]]|\\\\[\\x01-\\x7f])+)\\])(?(angle)>)$";

		/// <summary>
		/// Catches 90% of bad emails and works with the Javascript Regex engine
		/// </summary>
		public const string EmailAddressJavascriptFriendly = @"^[A-Za-z0-9._%-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,4}$";

