		/// <summary>
		/// United States telephone number. This field is a constant.
		/// </summary>
		/// This field returns "(?:(?&lt;Country&gt;[+1-9]*))\s*(?:(?:\()(?&lt;AreaCode&gt;[2-9]\d{2})(?:\))\ ?|(?&lt;AreaCode&gt;[2-9]\d{2})(?:\-?|\ ?))(?&lt;LocalNumber&gt;[2-9]\d{2}[- ]?\d{4})\s*(?:(?:[xX])?(?&lt;Extension&gt;\d*))".
		public const string UnitedStatesTelephoneNumber = @"(?:(?<Country>[+1-9]*))\s*(?:(?:\()(?<AreaCode>[2-9]\d{2})(?:\))\ ?|(?<AreaCode>[2-9]\d{2})(?:\-?|\ ?))(?<LocalNumber>[2-9]\d{2}[- ]?\d{4})\s*(?:(?:[xX])?(?<Extension>\d*))";

		/// <summary>
		/// Standard credit card number.  This field is a constant.
		/// </summary>
		public const string CreditCard = @"^((\d{4}[- ]?){3}\d{4})$";

		/// <summary>
		/// Standard credit card number.  This field is a constant.
		/// </summary>
		public const string CreditCardAmex = @"^((\d{4}[- ]?){3}\d{3})$";

		/// <summary>
		/// Credit Card Number Placeholder
		/// </summary>
		public const string CreditCardPlaceholder = @"^((#{4}\-){3}\d{4})$";

		/// <summary>
		/// Credit Card Number Placeholder.  Matches ####-####-###0-123
		/// </summary>
		public const string CreditCardAmexPlaceholder = @"^(#{4}-){2}#{3}\d-\d{3}$";

		/// <summary>
		/// Standard ip address.  This field is a constant.
		/// </summary>
		public const string IpAddress = @"^([0-2]?[0-5]?[0-5]\.){3}[0-2]?[0-5]?[0-5]$";

		/// <summary>
		/// CVV2 Code used by most credit cards (3-digit)
		/// </summary>
		public const string Cvv2 = @"^(?!000)\d{3}$";

		/// <summary>
		/// CVV2 Code used by American Express (4-digit)
		/// </summary>
		public const string Cvv2Amex = @"^(?!0000)\d{4}$";

		/// <summary>
		/// CVV2 Code Placeholder 
		/// </summary>
		public const string Cvv2Placeholder = @"^(#{3})$";

		/// <summary>
		/// CVV2 Code Placeholder 
		/// </summary>
		public const string Cvv2AmexPlaceholder = @"^(#{4})$";

		/// <summary>
		/// All numeric
		/// </summary>
		public const string Digits = @"^\d+$";
	}
}